import { PayloadAction, createSlice } from "@reduxjs/toolkit";

export interface Todo {
  id: string;
  message: string;
  sortOrder: number;
}

const todos = createSlice({
  name: "todos",
  initialState: [] as Todo[],
  reducers: {
    addTodo: (state, action: PayloadAction<string>) => {
      state.push({
        id: new Date().toISOString(),
        message: action.payload,
        sortOrder: state.length,
      })
    },
    deleteTodo: (state, action: PayloadAction<{ id: string }>) => {
      return state.filter((todo) => todo.id !== action.payload.id)
    },
    editTodo: (state, action: PayloadAction<{ id: string, message: string }>) => {
      const { id, message } = action.payload;
      const todo = state.find((todo) => todo.id === id);
      if (todo) {
        todo.message = message;
      } else {
        return state;
      }
    },
    sort: (state, action: PayloadAction<{ id: string, newPosition: number }>) => {
      const { id, newPosition } = action.payload;
      const activeTodo = state.find((todo) => todo.id === id);
      if (activeTodo) {
        const currentPosition = activeTodo.sortOrder;
        if (currentPosition > newPosition) {
          // move up
          state.forEach((todo) => {
            if (todo.sortOrder >= newPosition && todo.sortOrder < currentPosition + 1) {
              todo.sortOrder = todo.sortOrder + 1; // move down
            } else if (todo.sortOrder === currentPosition) {
              todo.sortOrder = newPosition; // move to new position
            }
          })

          state.splice(newPosition, 0, activeTodo); // insert at new position
          state.splice(currentPosition, 1); // remove from old position
        } else {
          // move down
          state.forEach((todo) => {
            if (todo.sortOrder <= newPosition && todo.sortOrder > currentPosition - 1) {
              todo.sortOrder = todo.sortOrder - 1; // move up
            } else if (todo.sortOrder === currentPosition) {
              todo.sortOrder = newPosition; // move to new position
            }
          })
          state.splice(newPosition, 0, activeTodo); // insert at new position
          state.splice(currentPosition, 1); // remove from old position
        }
      }
    }
  }
});

export const { addTodo, deleteTodo, editTodo, sort } = todos.actions;

export default todos;

