import { PayloadAction, createSlice } from "@reduxjs/toolkit";

export interface Todo {
  id: string;
  message: string;
}

const todos = createSlice({
  name: "todos",
  initialState: [] as Todo[],
  reducers: {
    addTodo: (state, action: PayloadAction<string>) => {
      state.push({
        id: new Date().toISOString(),
        message: action.payload,
      })
    },
    deleteTodo: (state, action: PayloadAction<{ id: string }>) => {
      return state.filter((todo) => todo.id !== action.payload.id)
    },
    editTodo: (state, action: PayloadAction<{ id: string, message: string }>) => {
      const { id, message } = action.payload;
      const todo = state.find((todo) => todo.id === id);
      if (todo) {
        todo.message = message;
      } else {
        return state;
      }
    },
    sort: (state, action: PayloadAction<{ id: string, index: number }>) => {
      const { id, index } = action.payload;
      const todo = state.find((todo) => todo.id === id);
      if (todo) {
        const currentIndex = state.indexOf(todo);
        state.splice(currentIndex, 1);
        state.splice(index, 0, todo);
      } else {
        return state;
      }
    }
  }
})

export const { addTodo, deleteTodo, sort } = todos.actions;

export default todos;
