import React from 'react';
import { useAppDispatch, useAppSelector } from '../../app/hooks';
import { AppDispatch, RootState } from '../../app/store';
import { editTask, deleteTask } from './taskSlice';

const  TaskList: React.FC = () => {
  const dispatch: AppDispatch = useAppDispatch()
  const  taskList = useAppSelector((state: RootState)=> state.tasks);
  const [tasks, setTasks] = React.useState(taskList); 
  // const [draggedTask, setDraggedTask] = React.useState(null);
// 
  return (
    <div>
      {tasks.map((task) => (
        <div 
          key={task.id}
          onDragStart={e => {console.log('drag start')}}
          onDragEnd={e => {console.log('drag end')}}
          draggable
        >
          <input
            type="text"
            value={task.message}
            onChange={(e) => dispatch(editTask({ id: task.id, message: e.target.value }))} />
          <button onClick={() => dispatch(deleteTask({id: task.id}))}>X</button>
        </div>
      ))}
    </div>
  )
}

export default  TaskList;
