import { useRef, useState, useEffect, SetStateAction } from 'react';
import { useNavigate } from 'react-router-dom';

import { useDispatch } from 'react-redux';
import { setCredentials } from './authSlice';
import { useLoginMutation } from './authApiSlice';

const Login = () => {
  const userRef = useRef<HTMLInputElement | null>(null);
  const errRef = useRef<HTMLDivElement | null>(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [errMsg, setErrMsg] = useState('');
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<'login' | 'signUp'>('login');


  const [login, { isLoading }] = useLoginMutation()
  const dispatch = useDispatch()

  useEffect(() => {
    userRef.current?.focus()
  }, [])

  useEffect(() => {
    setErrMsg('')
  }, [email, password])

  const handleSubmit = async (e:any) => {
    // e.preventDefault()

    const submitData = activeTab === 'login' ? { 
      email, 
      password 
    } : { 
      email,
      password,
      firstName,
      lastName 
    };

    try {
      const data = await login(submitData).unwrap()
      dispatch(setCredentials({ ...data, user: email }));
      setEmail('');
      setPassword('');
      navigate('/task');
    } catch (err) {
      setErrMsg('Login Failed');
      errRef.current?.focus();
    }
  }

  const handleEmailInput = (e: { target: { value: SetStateAction<string>; }; }) => setEmail(e.target.value)
  const handlePasswordInput = (e: { target: { value: SetStateAction<string>; }; }) => setPassword(e.target.value)
  const handleFirstNameInput = (e: { target: { value: SetStateAction<string>; }; }) => setFirstName(e.target.value)
  const handleLastNameInput = (e: { target: { value: SetStateAction<string>; }; }) => setLastName(e.target.value)

  const LoginForm: React.FC = () => {
   return (
      <section className="login">
        <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
        <form onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input
            type="text"
            id="email"
            ref={userRef}
            value={email}
            onChange={handleEmailInput}
            autoComplete="off"
            required
          />

          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            onChange={handlePasswordInput}
            value={password}
            required
          />
          <button>Sign In</button>
        </form>
      </section>
    )
  }

  const SignUpForm: React.FC = () => {
    return (
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="firstName">First Name</label>
          <input id="firstName" type="text" value={firstName} onChange={handleFirstNameInput} required />
        </div>
        <div>
          <label htmlFor="lastName">Last Name</label>
          <input id="lastName" type="text" value={lastName} onChange={handleLastNameInput} required />
        </div>
        <div>
          <label htmlFor="signUpEmail">Email</label>
          <input id="signUpEmail" type="email" value={email} onChange={handleEmailInput} required />
        </div>
        <div>
          <label htmlFor="signUpPassword">Password</label>
          <input id="signUpPassword" type="password" value={password} onChange={handlePasswordInput} required />
        </div>
        <button type="submit">Sign Up</button>
      </form>
    );
  }

  return isLoading ? <h1>Loading...</h1> : (
    <div className="app">
      <div className="tabs">
        <button
          className={`tab ${activeTab === 'login' ? 'active' : ''}`}
          onClick={() => setActiveTab('login')}
        >
          Login
        </button>
        <button
          className={`tab ${activeTab === 'signUp' ? 'active' : ''}`}
          onClick={() => setActiveTab('signUp')}
        >
          Sign Up
        </button>
      </div>
      <div className="form">
        {activeTab === 'login' && <LoginForm />}
        {activeTab === 'signUp' && <SignUpForm />}
      </div>
    </div>
  );
}
export default Login
