import React from 'react';
import { useAppDispatch, useAppSelector } from '../../app/hooks';
import { AppDispatch, RootState } from '../../app/store';
import todos from './todoSlice';
import { addTodo, deleteTodo, sort } from './todoSlice';

const TodoList: React.FC = () => {
  const dispatch: AppDispatch = useAppDispatch()
  const todoList = useAppSelector((state: RootState)=> state.todos);

  return (
    <div>
      {todoList.map((todo) => (
        <div key={todo.id}>
          <input
            type="text"
            value={todo.message}
            onChange={(e) => dispatch(todos.actions.edit({ id: todo.id, title: e.target.value }))} />
          <button onClick={() => dispatch(deleteTodo(todo.id))}>X</button>
        </div>
      ))}
    </div>
  )
}

export default TodoList;
