import { createAsyncThunk } from '@reduxjs/toolkit';
import { Task, addTasks } from './taskSlice';

const options = {
  "method": "GET",
  "headers": {
    "Content-Type": "application/x-www-form-urlencoded",
    "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjEsImVtYWlsIjoiYUBnbWFpbC5jb20iLCJpYXQiOjE3MTgwMzU1NDYsImV4cCI6MTcxODEyMTk0Nn0.fXpP2RJHTXSAD2SyFQfhnBlU0NasJgnbESEtN-NxpqE",
    "Content-Length": "0"
  }
};

export const fetchTasks = createAsyncThunk<Task[], void, { rejectValue: { error: string } }>(
  'tasks/fetchTasks',
  async (_, { dispatch, rejectWithValue }) => {
    try {
      const response = await fetch('http://localhost:3333/tasks', options);
      if (!response.ok) {
        return rejectWithValue({ error: 'Network response was not ok' });
      }
      const data: Task[] = await response.json();;

      console.log(data)
      // dispatch(addTasks(data)); // dispatch the action to add tasks
      return data;
    } catch (error: any) {
      return rejectWithValue({ error: error.message });
    }
  }
);
