import { createAsyncThunk } from "@reduxjs/toolkit";
import { apiSlice } from "../../app/api/apiSlice";
import { Task } from "../task/taskSlice";
import { RootState } from "@reduxjs/toolkit/query";
import { setCredentials } from "./authSlice";

export const authApiSlice = apiSlice.injectEndpoints({
    endpoints: builder => ({
        login: builder.mutation({
            query: credentials => {
              return {
                url: 'auth/signup',
                method: 'POST',
                body: { ...credentials },
                headers: {
                  'Content-Type': 'application/json'
                }
              }  
            }
        }),
    })
})

export const {
    useLoginMutation
} = authApiSlice

export const signUp = createAsyncThunk<Task[], void, { rejectValue: { error: string } }>(
  'tasks/signUp',
  async (_, { dispatch, rejectWithValue }) => {
    try {
      const response = await fetch('http://localhost:3333/auth/signup', {
        method: "POST", 
        headers: {
          "Content-Type": "application/x-wwww-form-urlencoded",
          "Content-Length": "0"
        },
        body: JSON.stringify({
          email:"z@gmail.com",
          password:"123"
        })
      });
      if (!response.ok) {
        return rejectWithValue({ error: 'Network response was not ok' });
      }
      const data = await response.json();;
      console.log({data})

      // const result = data.map(({id, message, sortOrder}) => ({id, message, sortOrder}))
      // dispatch(setCredentials(result));
      return data;
    } catch (error: any) {
      return rejectWithValue({ error: error.message });
    }
  }
);
